  border: '#9e9e9e',
  text: '#424242',
  accent: '#9e9e9e'
};
const now = Date.now();
function uid() { return Math.random().toString(16).slice(2,10); }
function snap(v){ return Math.round(v/20)*20; }
const BASE = {
  angle: 0,
  strokeColor: THEME.border,
  backgroundColor: THEME.background,
  fillStyle: 'solid',
  strokeWidth: 2,
  strokeStyle: 'solid',
  roughness: 0,
  opacity: 100,
  seed: 1,
  version: 1,
  versionNonce: 1,
  isDeleted: false,
  updated: now,
  link: null,
  locked: false,
};
function rect(x,y,w,h,roundness=null,bg=null,stroke=null,group=null,textId=null){
  const el = {
    id: uid(),
    type: 'rectangle',
    x: snap(x),
    y: snap(y),
    width: snap(w),
    height: snap(h),
    ...BASE,
    backgroundColor: bg || THEME.background,
    strokeColor: stroke || THEME.border,
    groupIds: group ? [group] : [],
    boundElements: textId ? [{type:'text', id:textId}] : [],
  };
  if (roundness !== null) el.roundness = {type:3, value: roundness};
  return el;
}
function textEl(x,y,text,fontSize=16,containerId=null,group=null,align='center',valign='middle'){
  const width = Math.round(((text.length * fontSize * 0.6) + 20)/10)*10;
  const height = fontSize + 10;
  return {
    id: uid(),
    type: 'text',
    x: snap(x),
    y: snap(y),
    width,
    height,
    ...BASE,
    strokeColor: THEME.text,
    backgroundColor: 'transparent',
    fillStyle: 'solid',
    fontSize,
    fontFamily: 1,
    text,
    textAlign: align,
    verticalAlign: valign,
    baseline: fontSize,
    containerId,
    groupIds: group ? [group] : [],
  };
}
function labelOver(x,y,text){
  return textEl(x,y,text,18,null,null,'left','top');
}
function arrow(x1,y1,x2,y2,startId,endId){
  return {
    id: uid(),
    type: 'arrow',
    x: snap(x1),
    y: snap(y1),
    width: snap(x2-x1),
    height: snap(y2-y1),
    ...BASE,
    strokeColor: THEME.accent,
    points: [[0,0],[snap(x2-x1), snap(y2-y1)]],
    startBinding: {elementId: startId, focus: 0, gap: 10},
    endBinding: {elementId: endId, focus: 0, gap: 10},
  };
}

