const fs = require('fs');
const path = require('path');
const IN = '/home/edouard/test-framework/_bmad-output/excalidraw-diagrams/wireframe-20260130T000000.excalidraw';
const OUT_DIR = '/home/edouard/test-framework/_bmad-output/excalidraw-diagrams/screens';
fs.mkdirSync(OUT_DIR, {recursive: true});

const data = JSON.parse(fs.readFileSync(IN,'utf8'));
const elements = data.elements || [];

// Identify screens
const screens = [];
for (const el of elements){
  if (el.type === 'rectangle' && el.width === 800 && el.height === 600){
    const label = elements.find(t=>t.type==='text' && Math.abs(t.x - el.x) <= 20 && Math.abs(t.y - (el.y-30)) <= 20);
    if (label) screens.push({title: label.text, x: el.x, y: el.y, id: el.id});
  }
}

function slug(title){
  return title.replace(/[^a-zA-Z0-9]+/g,'-').replace(/^-+|-+$/g,'');
}

// Action links map
const linkMap = new Map([
  ['Run Daily', 'Run Progress'],
  ['Next: Review', 'CR Review'],
  ['Send', 'Reporting Hebdo (PPT)'],
  ['Generate PPT', 'Reporting Hebdo (PPT)'],
  ['Publish', 'TNR / GO-NO-GO'],
  ['Clarify Tasks', 'Anomaly Drafts'],
  ['Go List', 'CR Review'],
  ['New Draft', 'Anomaly Drafts'],
  ['Continue Run', 'Run Progress'],
  ['Replay', 'Run Progress'],
  ['Save', 'Dashboard / Run Start'],
]);

const screenByTitle = Object.fromEntries(screens.map(s=>[s.title, s]));

function filterElementsForScreen(s){
  const minX = s.x - 40;
  const minY = s.y - 60;
  const maxX = s.x + 800 + 40;
  const maxY = s.y + 600 + 40;

  const keep = new Set();
  for (const el of elements){
    if (el.x >= minX && el.x <= maxX && el.y >= minY && el.y <= maxY){
      keep.add(el.id);
      if (el.containerId) keep.add(el.containerId);
    }
  }

  // Also keep text labels above screen
  for (const el of elements){
    if (el.type === 'text' && Math.abs(el.x - s.x) <= 20 && Math.abs(el.y - (s.y-30)) <= 20){
      keep.add(el.id);
    }
  }

  return elements.filter(el => keep.has(el.id));
}

function applyLinks(els){
  const byId = Object.fromEntries(els.map(e=>[e.id,e]));
  for (const el of els){
    if (el.type === 'text' && linkMap.has(el.text)){
      const target = linkMap.get(el.text);
      const targetScreen = screenByTitle[target];
      if (!targetScreen) continue;
      const targetFile = `./${slug(target)}.excalidraw`;
      el.link = targetFile;
      if (el.containerId && byId[el.containerId]) byId[el.containerId].link = targetFile;
    }
  }
}

// Write per-screen files
for (const s of screens){
  let els = filterElementsForScreen(s);
  applyLinks(els);
  const payload = { type: 'excalidraw', version: 2, source: 'https://excalidraw.com', elements: els };
  const file = path.join(OUT_DIR, `${slug(s.title)}.excalidraw`);
  fs.writeFileSync(file, JSON.stringify(payload));
}

// Update main file links to point to screen files
const mainEls = JSON.parse(fs.readFileSync(IN,'utf8')).elements;
const byIdMain = Object.fromEntries(mainEls.map(e=>[e.id,e]));
for (const el of mainEls){
  if (el.type === 'text' && linkMap.has(el.text)){
    const target = linkMap.get(el.text);
    const targetFile = `./screens/${slug(target)}.excalidraw`;
    el.link = targetFile;
    if (el.containerId && byIdMain[el.containerId]) byIdMain[el.containerId].link = targetFile;
  }
}
fs.writeFileSync(IN, JSON.stringify({ ...data, elements: mainEls }));

console.log('screens:', screens.length);
