  background: '#ffffff',
  container: '#f5f5f5',
  border: '#9e9e9e',
  text: '#424242',
  accent: '#9e9e9e'
};
const now = Date.now();
function uid(){ return Math.random().toString(16).slice(2,10); }
function snap(v){ return Math.round(v/20)*20; }
const BASE = {
  angle: 0, strokeColor: THEME.border, backgroundColor: THEME.background,
  fillStyle: 'solid', strokeWidth: 2, strokeStyle: 'solid', roughness: 0,
  opacity: 100, seed: 1, version: 1, versionNonce: 1, isDeleted: false,
  updated: now, link: null, locked: false,
};
function rect(x,y,w,h,roundness=6,bg='#ffffff',stroke=null,group=null){
  const el = { id: uid(), type:'rectangle', x:snap(x), y:snap(y), width:snap(w), height:snap(h), ...BASE,
    backgroundColor:bg, strokeColor: stroke||THEME.border, groupIds: group?[group]:[], boundElements:[] };
  if (roundness !== null) el.roundness = {type:3, value: roundness};
  return el;
}
function textEl(x,y,text,fontSize=12,containerId=null,group=null,align='left',valign='middle'){
  const width = Math.round(((text.length * fontSize * 0.6) + 20)/10)*10;
  const height = fontSize + 10;
  return { id: uid(), type:'text', x:snap(x), y:snap(y), width, height, ...BASE,
    strokeColor: THEME.text, backgroundColor:'transparent', fillStyle:'solid', fontSize, fontFamily:1,
    text, textAlign: align, verticalAlign: valign, baseline: fontSize, containerId, groupIds: group?[group]:[] };
}
function labeledBox(x,y,w,h,label,fontSize=12){
  const gid = uid();
  const box = rect(x,y,w,h,6,'#ffffff',null,gid);
  const txt = textEl(x+10,y+10,label,fontSize,box.id,gid,'left','top');
  box.boundElements.push({type:'text', id: txt.id});
  return [box, txt];
}
function load(file){
  return JSON.parse(fs.readFileSync(path.join(DIR,file),'utf8'));
}
function save(file,data){
