      el.fontSize = 18;
      el.textAlign = 'left';
      el.verticalAlign = 'top';
      continue;
    }
    // Header title inside header bar
    if (el.containerId){
      const parent = els.find(e=>e.id === el.containerId);
      if (parent && parent.x === sx && parent.y === sy && parent.width === 800 && parent.height === 60){
        el.fontSize = 16;
        el.textAlign = 'left';
        el.verticalAlign = 'middle';
        continue;
      }
    }
    // All other labels standardized
    el.fontSize = 12;
    el.textAlign = 'left';
    el.verticalAlign = 'top';
  }

