   330	/// Result of parsing a serde error for user-friendly transformation
   331	enum SerdeErrorKind {
   332	    /// Missing required field
   333	    MissingField { field: &'static str, hint: &'static str },
   334	    /// Invalid enum variant
   335	    InvalidEnumValue { field: &'static str, reason: &'static str, hint: &'static str },
   336	    /// Unknown field (when deny_unknown_fields is active)
   337	    UnknownField { field: String, location: &'static str, hint: &'static str },
   338	}
   339	
   340	/// Parse serde error message to extract field info and provide helpful hints.
   341	///
   342	/// # Supported Error Types
   343	///
   344	/// - Missing required fields (project_name, output_folder, endpoint)
   345	/// - Invalid enum variants (llm.mode)
   346	/// - Unknown fields (when deny_unknown_fields is active)
   347	/// - Invalid type errors (string expected, etc.)
   348	///
   349	/// # Limitations
   350	///
   351	/// This function uses string pattern matching to identify known fields. Unknown or
   352	/// deeply nested fields not explicitly mapped will fall through to the generic
   353	/// ParseError. This is a trade-off: explicit hints for common errors vs. generic
   354	/// messages for edge cases. The patterns are based on serde_yaml error message format.
   355	fn parse_serde_error(err_msg: &str) -> Option<SerdeErrorKind> {
   356	    // Handle missing field errors: "missing field `field_name`"
   357	    if err_msg.contains("missing field") {
   358	        if err_msg.contains("`project_name`") {
   359	            return Some(SerdeErrorKind::MissingField {
   360	                field: "project_name",
   361	                hint: "a non-empty project name string (e.g., 'my-project')",
   362	            });
   363	        }
   364	        if err_msg.contains("`output_folder`") {
   365	            return Some(SerdeErrorKind::MissingField {
   366	                field: "output_folder",
   367	                hint: "an output folder path (e.g., './output' or '/var/data')",
   368	            });
   369	        }
   370	        if err_msg.contains("`endpoint`") {
   371	            // Could be jira.endpoint or squash.endpoint
   372	            if err_msg.contains("jira") || err_msg.to_lowercase().contains("jira") {
   373	                return Some(SerdeErrorKind::MissingField {
   374	                    field: "jira.endpoint",
   375	                    hint: "a Jira server URL (e.g., 'https://jira.example.com')",
   376	                });
   377	            }
   378	            if err_msg.contains("squash") || err_msg.to_lowercase().contains("squash") {
   379	                return Some(SerdeErrorKind::MissingField {
   380	                    field: "squash.endpoint",
   381	                    hint: "a Squash server URL (e.g., 'https://squash.example.com')",
   382	                });
   383	            }
   384	            // Generic endpoint
   385	            return Some(SerdeErrorKind::MissingField {
   386	                field: "endpoint",
   387	                hint: "a valid URL endpoint",
   388	            });
   389	        }
   390	    }
   391	
   392	    // Handle invalid enum variant errors for LlmMode
   393	    // serde_yaml format: "unknown variant `invalid`, expected one of `auto`, `local`, `cloud`"
   394	    if err_msg.contains("unknown variant") && (err_msg.contains("auto") || err_msg.contains("local") || err_msg.contains("cloud")) {
   395	        return Some(SerdeErrorKind::InvalidEnumValue {
   396	            field: "llm.mode",
   397	            reason: "is not a valid mode",
   398	            hint: "one of: 'auto', 'local', or 'cloud'",
   399	        });
   400	    }
   401	
   402	    // Handle unknown field errors (deny_unknown_fields): "unknown field `field_name`"
   403	    if err_msg.contains("unknown field") {
   404	        // Extract the field name from backticks
   405	        if let Some(start) = err_msg.find("unknown field `") {
   406	            let after_prefix = &err_msg[start + 15..]; // len("unknown field `") = 15
   407	            if let Some(end) = after_prefix.find('`') {
   408	                let field_name = after_prefix[..end].to_string();
   409	
   410	                // Determine the location based on context in error message
   411	                let location = if err_msg.contains("jira") || err_msg.to_lowercase().contains("jira") {
   412	                    "jira"
   413	                } else if err_msg.contains("squash") || err_msg.to_lowercase().contains("squash") {
   414	                    "squash"
   415	                } else if err_msg.contains("llm") || err_msg.to_lowercase().contains("llm") {
   416	                    "llm"
   417	                } else if err_msg.contains("templates") || err_msg.to_lowercase().contains("templates") {
   418	                    "templates"
   419	                } else {
   420	                    "root" // Top-level unknown field

Exit code: 0
Wall time: 0.2 seconds
Output:
   420	                    "root" // Top-level unknown field
   421	                };
   422	
   423	                let hint = match location {
   424	                    "jira" => "valid jira fields are: endpoint, token",
   425	                    "squash" => "valid squash fields are: endpoint, username, password",
   426	                    "llm" => "valid llm fields are: mode, local_endpoint, local_model, cloud_enabled, cloud_endpoint, cloud_model, api_key, timeout_seconds, max_tokens",
   427	                    "templates" => "valid templates fields are: cr, ppt, anomaly",
   428	                    _ => "valid root fields are: project_name, output_folder, jira, squash, templates, llm",
   429	                };
   430	
   431	                return Some(SerdeErrorKind::UnknownField {
   432	                    field: field_name,
   433	                    location,
   434	                    hint,
   435	                });
   436	            }
   437	        }
   438	    }
   439	
   440	    // Handle type errors: "invalid type: found X, expected Y"
   441	    if err_msg.contains("invalid type") {
   442	        // Check for common type mismatches
   443	        if err_msg.contains("project_name") || (err_msg.contains("expected a string") && err_msg.len() < 100) {
   444	            return Some(SerdeErrorKind::InvalidEnumValue {
   445	                field: "project_name",
   446	                reason: "has invalid type",
   447	                hint: "a string value (e.g., project_name: \"my-project\")",
   448	            });
   449	        }
   450	    }
   451	
   452	    None
   453	}
