   455	/// Validate that a URL has a valid format (scheme + valid host at minimum)
   456	///
   457	/// A valid host must be either:
   458	/// - "localhost" (with optional port)
   459	/// - A domain with at least one dot (e.g., "example.com", "api.example.com")
   460	/// - An IPv4 address (contains dots)
   461	/// - An IPv6 address in brackets (e.g., "[::1]", "[2001:db8::1]")
   462	///
   463	/// Invalid hosts like "a", "...", or random strings are rejected.
   464	/// Ports must be valid (1-65535).
   465	fn is_valid_url(url: &str) -> bool {
   466	    // Must start with http:// or https://
   467	    let without_scheme = if let Some(rest) = url.strip_prefix("https://") {
   468	        rest
   469	    } else if let Some(rest) = url.strip_prefix("http://") {
   470	        rest
   471	    } else {
   472	        return false;
   473	    };
   474	
   475	    // Must have at least one character after the scheme (the host)
   476	    // and the host must not be empty or just whitespace
   477	    let trimmed = without_scheme.trim();
   478	    if trimmed.is_empty() {
   479	        return false;
   480	    }
   481	
   482	    // Extract host and port (before path)
   483	    let host_port = trimmed.split('/').next().unwrap_or(trimmed);
   484	
   485	    // Handle IPv6 addresses: [::1] or [::1]:8080
   486	    if host_port.starts_with('[') {
   487	        // Find closing bracket
   488	        if let Some(bracket_end) = host_port.find(']') {
   489	            let ipv6_addr = &host_port[1..bracket_end];
   490	
   491	            // Basic IPv6 validation: must contain colons, allow % for zone ID
   492	            if ipv6_addr.is_empty() || (!ipv6_addr.contains(':') && !ipv6_addr.contains('%')) {
   493	                return false;
   494	            }
   495	
   496	            // Check for port after bracket
   497	            let after_bracket = &host_port[bracket_end + 1..];
   498	            if !after_bracket.is_empty() {
   499	                // Must be :port
   500	                if let Some(port_str) = after_bracket.strip_prefix(':') {
   501	                    if !port_str.is_empty() {
   502	                        match port_str.parse::<u32>() {
   503	                            Ok(p) if (1..=65535).contains(&p) => {}
   504	                            _ => return false, // Invalid port number
   505	                        }
   506	                    }
   507	                } else {
   508	                    return false; // Invalid format after bracket
   509	                }
   510	            }
   511	            return true;
   512	        } else {
   513	            return false; // Unclosed bracket
   514	        }
   515	    }
   516	
   517	    // Non-IPv6: Check for port and validate it
   518	    let (host, port_str) = if let Some(colon_pos) = host_port.rfind(':') {
   519	        (&host_port[..colon_pos], Some(&host_port[colon_pos + 1..]))
   520	    } else {
   521	        (host_port, None)
   522	    };
   523	
   524	    // Validate port if present (1-65535 range)
   525	    if let Some(port) = port_str {
   526	        if !port.is_empty() {
   527	            match port.parse::<u32>() {
   528	                Ok(p) if (1..=65535).contains(&p) => {}
   529	                _ => return false, // Invalid port number
   530	            }
   531	        }
   532	    }
   533	
   534	    // Host must not be empty after extraction
   535	    if host.is_empty() {
   536	        return false;
   537	    }
   538	
   539	    // Accept "localhost" as valid
   540	    if host.eq_ignore_ascii_case("localhost") {
   541	        return true;
   542	    }
   543	
   544	    // For other hosts, require at least one dot (domain or IP)
   545	    // and the dot cannot be at the start/end, and no consecutive dots
   546	    if !host.contains('.') {
   547	        return false;
   548	    }
   549	
   550	    // Reject hosts that are just dots (e.g., "...", ".")
   551	    if host.chars().all(|c| c == '.') {
   552	        return false;
   553	    }
   554	
   555	    // Reject hosts starting or ending with dot
   556	    if host.starts_with('.') || host.ends_with('.') {
   557	        return false;
   558	    }
   559	
   560	    // Reject consecutive dots (e.g., "example..com")
   561	    if host.contains("..") {
   562	        return false;
   563	    }
   564	
   565	    // Validate each label in the hostname (RFC 1123 compliant):
   566	    // - Labels separated by dots
   567	    // - Each label: 1-63 chars, alphanumeric + hyphens
   568	    // - Labels cannot start or end with hyphen
   569	    for label in host.split('.') {
   570	        if label.is_empty() || label.len() > 63 {
   571	            return false;
   572	        }
   573	        // Label cannot start or end with hyphen
   574	        if label.starts_with('-') || label.ends_with('-') {
   575	            return false;
   576	        }
   577	        // Label must contain only alphanumeric and hyphens
   578	        // Exception: allow all-numeric labels for IP addresses
   579	        if !label.chars().all(|c| c.is_ascii_alphanumeric() || c == '-') {
   580	            return false;
   581	        }
   582	    }
   583	
   584	    true
   585	}
