   592	/// Validate that a project name contains only valid characters
   593	/// Valid: alphanumeric, hyphens (-), underscores (_)
   594	fn is_valid_project_name(name: &str) -> bool {
   595	    !name.is_empty()
   596	        && name
   597	            .chars()
   598	            .all(|c| c.is_ascii_alphanumeric() || c == '-' || c == '_')
   599	}
