   625	/// Validate configuration fields
   626	fn validate_config(config: &ProjectConfig) -> Result<(), ConfigError> {
   627	    // Validate project_name is not empty
   628	    if config.project_name.trim().is_empty() {
   629	        return Err(ConfigError::invalid_value(
   630	            "project_name",
   631	            "cannot be empty",
   632	            "a non-empty project name string",
   633	        ));
   634	    }
   635	
   636	    // Validate project_name format (alphanumeric + hyphens + underscores only)
   637	    if !is_valid_project_name(&config.project_name) {
   638	        return Err(ConfigError::invalid_value(
   639	            "project_name",
   640	            "contains invalid characters",
   641	            "alphanumeric characters, hyphens (-), and underscores (_) only (e.g., 'my-project' or 'test_framework')",
   642	        ));
   643	    }
   644	
   645	    // Validate output_folder is not empty and has valid format
   646	    if !is_valid_path_format(&config.output_folder) {
   647	        return Err(ConfigError::invalid_value(
   648	            "output_folder",
   649	            "cannot be empty or contain invalid characters",
   650	            "a valid output folder path (e.g., './output' or '/var/data')",
   651	        ));
   652	    }
   653	
   654	    // Validate output_folder does not contain path traversal
   655	    if !is_safe_path(&config.output_folder) {
   656	        return Err(ConfigError::invalid_value(
   657	            "output_folder",
   658	            "cannot contain path traversal sequences (..)",
   659	            "a direct path without '..' (e.g., './output' or '/var/data')",
   660	        ));
   661	    }
   662	
   663	    // Validate LLM config if present
   664	    if let Some(ref llm) = config.llm {
   665	        // If mode is local, local_endpoint should be provided
   666	        if llm.mode == LlmMode::Local && llm.local_endpoint.is_none() {
   667	            return Err(ConfigError::missing_field(
   668	                "llm.local_endpoint",
   669	                "a local LLM endpoint URL (e.g., http://localhost:11434) when mode is 'local'",
   670	            ));
   671	        }
   672	
   673	        // If mode is cloud, api_key is required
   674	        if llm.mode == LlmMode::Cloud && llm.api_key.is_none() {
   675	            return Err(ConfigError::missing_field(
   676	                "llm.api_key",
   677	                "an API key for cloud LLM when mode is 'cloud'",
   678	            ));
   679	        }
   680	
   681	        // If mode is cloud and cloud_enabled is false, that's a configuration error
   682	        if llm.mode == LlmMode::Cloud && !llm.cloud_enabled {
   683	            return Err(ConfigError::invalid_value(
   684	                "llm.cloud_enabled",
   685	                "must be true when mode is 'cloud'",
   686	                "set cloud_enabled: true when using cloud mode",
   687	            ));
   688	        }
   689	
   690	        // Validate local_endpoint URL format if provided
   691	        if let Some(ref endpoint) = llm.local_endpoint {
   692	            if !is_valid_url(endpoint) {
   693	                return Err(ConfigError::invalid_value(
   694	                    "llm.local_endpoint",
   695	                    "must be a valid URL with host",
   696	                    "a URL like http://localhost:11434",
   697	                ));
   698	            }
   699	        }
   700	
   701	        // Validate cloud_endpoint URL format if provided
   702	        if let Some(ref endpoint) = llm.cloud_endpoint {
   703	            if !is_valid_url(endpoint) {
   704	                return Err(ConfigError::invalid_value(
   705	                    "llm.cloud_endpoint",
   706	                    "must be a valid URL with host",
   707	                    "a URL like https://api.openai.com/v1",
   708	                ));
   709	            }
   710	        }
   711	    }
   712	
   713	    // Validate Jira endpoint URL format if present
   714	    if let Some(ref jira) = config.jira {
   715	        if !is_valid_url(&jira.endpoint) {
   716	            return Err(ConfigError::invalid_value(
   717	                "jira.endpoint",
   718	                "must be a valid URL with host",
   719	                "a URL like https://jira.example.com",
   720	            ));
   721	        }
   722	    }
   723	
   724	    // Validate Squash endpoint URL format if present
   725	    if let Some(ref squash) = config.squash {
   726	        if !is_valid_url(&squash.endpoint) {
   727	            return Err(ConfigError::invalid_value(
   728	                "squash.endpoint",
   729	                "must be a valid URL with host",
   730	                "a URL like https://squash.example.com",
   731	            ));
   732	        }
   733	    }
   734	
   735	    // Validate template paths if provided
   736	    if let Some(ref templates) = config.templates {
   737	        if let Some(ref cr) = templates.cr {
   738	            if !is_valid_path_format(cr) {
   739	                return Err(ConfigError::invalid_value(
   740	                    "templates.cr",
   741	                    "must be a valid file path",
   742	                    "a path like './templates/cr.md'",
   743	                ));
   744	            }
   745	            if !has_valid_extension(cr, &[".md"]) {
   746	                return Err(ConfigError::invalid_value(
   747	                    "templates.cr",
   748	                    "must be a Markdown file",
   749	                    "a .md file path like './templates/cr.md'",
   750	                ));
   751	            }
   752	        }
   753	        if let Some(ref ppt) = templates.ppt {
   754	            if !is_valid_path_format(ppt) {
   755	                return Err(ConfigError::invalid_value(
   756	                    "templates.ppt",
   757	                    "must be a valid file path",
   758	                    "a path like './templates/ppt.pptx'",
   759	                ));
   760	            }
   761	            if !has_valid_extension(ppt, &[".pptx"]) {
   762	                return Err(ConfigError::invalid_value(
   763	                    "templates.ppt",
   764	                    "must be a PowerPoint file",
   765	                    "a .pptx file path like './templates/report.pptx'",
   766	                ));
   767	            }
   768	        }
   769	        if let Some(ref anomaly) = templates.anomaly {
   770	            if !is_valid_path_format(anomaly) {
   771	                return Err(ConfigError::invalid_value(
   772	                    "templates.anomaly",
   773	                    "must be a valid file path",
   774	                    "a path like './templates/anomaly.md'",
   775	                ));
   776	            }
   777	            if !has_valid_extension(anomaly, &[".md"]) {
   778	                return Err(ConfigError::invalid_value(
   779	                    "templates.anomaly",
   780	                    "must be a Markdown file",
   781	                    "a .md file path like './templates/anomaly.md'",
   782	                ));
   783	            }
   784	        }
   785	    }
   786	
   787	    Ok(())
   788	}
