pub mod error;
pub mod profiles;

pub use config::{
    load_config, JiraConfig, LlmConfig, LlmMode, ProjectConfig, Redact, SquashConfig, TemplatesConfig,
};
pub use error::ConfigError;

// Profile types are placeholders for Story 0.2 - hidden from public documentation
