   658	        // Single-word internal hostname: validate as a single label
   659	        if host.is_empty() || host.len() > 63 {
   660	            return false;
   661	        }
   662	        if host.starts_with('-') || host.ends_with('-') {
   663	            return false;
   664	        }
   665	        // Must be alphanumeric + hyphens only
   666	        return host.chars().all(|c| c.is_ascii_alphanumeric() || c == '-');
   667	    }
   668	
   669	    // Reject hosts that are just dots (e.g., "...", ".")
   670	    if host.chars().all(|c| c == '.') {
   671	        return false;
   672	    }
   673	
   674	    // Reject hosts starting or ending with dot
   675	    if host.starts_with('.') || host.ends_with('.') {
   676	        return false;
   677	    }
   678	
   679	    // Reject consecutive dots (e.g., "example..com")
   680	    if host.contains("..") {
   681	        return false;
   682	    }
   683	
   684	    // Validate each label in the hostname (RFC 1123 compliant):
   685	    // - Labels separated by dots
   686	    // - Each label: 1-63 chars, alphanumeric + hyphens
   687	    // - Labels cannot start or end with hyphen
   688	    for label in host.split('.') {
   689	        if label.is_empty() || label.len() > 63 {
   690	            return false;
   691	        }
   692	        // Label cannot start or end with hyphen
   693	        if label.starts_with('-') || label.ends_with('-') {
   694	            return false;
   695	        }
   696	        // Label must contain only alphanumeric and hyphens
   697	        // Exception: allow all-numeric labels for IP addresses
   698	        if !label.chars().all(|c| c.is_ascii_alphanumeric() || c == '-') {
   699	            return false;
   700	        }
   701	    }
   702	
   703	    true
   704	}
   705	
   706	/// Validate that a path string has valid format (not empty, no null bytes)
   707	fn is_valid_path_format(path: &str) -> bool {
   708	    !path.trim().is_empty() && !path.contains('\0')
   709	}
   710	
   711	/// Validate that a project name contains only valid characters
   712	/// Valid: alphanumeric, hyphens (-), underscores (_)
   713	fn is_valid_project_name(name: &str) -> bool {
   714	    !name.is_empty()
   715	        && name
   716	            .chars()
   717	            .all(|c| c.is_ascii_alphanumeric() || c == '-' || c == '_')
   718	}
   719	
   720	/// Validate that a path does not contain path traversal sequences (..)
   721	///
   722	/// This checks for ".." as a path component, not just any occurrence of "..".
   723	/// For example:
   724	/// - "../parent" is unsafe (traversal at start)
   725	/// - "./data/../secrets" is unsafe (traversal in middle)
   726	/// - "file..txt" is safe (not a path component)
   727	/// - "my..folder/data" is safe (not a path component)
   728	fn is_safe_path(path: &str) -> bool {
   729	    // Split by both Unix and Windows path separators
   730	    for component in path.split(['/', '\\']) {
   731	        if component == ".." {
   732	            return false;
   733	        }
   734	    }
   735	    true
   736	}
   737	
   738	/// Validate template file extension
   739	fn has_valid_extension(path: &str, expected_extensions: &[&str]) -> bool {
   740	    let lower = path.to_lowercase();
   741	    expected_extensions.iter().any(|ext| lower.ends_with(ext))
   742	}
