   744	/// Validate configuration fields
   745	fn validate_config(config: &ProjectConfig) -> Result<(), ConfigError> {
   746	    // Validate project_name is not empty
   747	    if config.project_name.trim().is_empty() {
   748	        return Err(ConfigError::invalid_value(
   749	            "project_name",
   750	            "cannot be empty",
   751	            "a non-empty project name string",
   752	        ));
   753	    }
   754	
   755	    // Validate project_name format (alphanumeric + hyphens + underscores only)
   756	    if !is_valid_project_name(&config.project_name) {
   757	        return Err(ConfigError::invalid_value(
   758	            "project_name",
   759	            "contains invalid characters",
   760	            "alphanumeric characters, hyphens (-), and underscores (_) only (e.g., 'my-project' or 'test_framework')",
   761	        ));
   762	    }
   763	
   764	    // Validate output_folder is not empty and has valid format
   765	    if !is_valid_path_format(&config.output_folder) {
   766	        return Err(ConfigError::invalid_value(
   767	            "output_folder",
   768	            "cannot be empty or contain invalid characters",
   769	            "a valid output folder path (e.g., './output' or '/var/data')",
   770	        ));
   771	    }
   772	
   773	    // Validate output_folder does not contain path traversal
   774	    if !is_safe_path(&config.output_folder) {
   775	        return Err(ConfigError::invalid_value(
   776	            "output_folder",
   777	            "cannot contain path traversal sequences (..)",
   778	            "a direct path without '..' (e.g., './output' or '/var/data')",
   779	        ));
   780	    }
   781	
   782	    // Validate LLM config if present
   783	    if let Some(ref llm) = config.llm {
   784	        // If mode is local, local_endpoint should be provided
   785	        if llm.mode == LlmMode::Local && llm.local_endpoint.is_none() {
   786	            return Err(ConfigError::missing_field(
   787	                "llm.local_endpoint",
   788	                "a local LLM endpoint URL (e.g., http://localhost:11434) when mode is 'local'",
   789	            ));
   790	        }
   791	
   792	        // If mode is cloud, api_key is required
   793	        if llm.mode == LlmMode::Cloud && llm.api_key.is_none() {
   794	            return Err(ConfigError::missing_field(
   795	                "llm.api_key",
   796	                "an API key for cloud LLM when mode is 'cloud'",
   797	            ));
   798	        }
   799	
   800	        // If mode is cloud and cloud_enabled is false, that's a configuration error
   801	        if llm.mode == LlmMode::Cloud && !llm.cloud_enabled {
   802	            return Err(ConfigError::invalid_value(
   803	                "llm.cloud_enabled",
   804	                "must be true when mode is 'cloud'",
   805	                "set cloud_enabled: true when using cloud mode",
   806	            ));
   807	        }
   808	
   809	        // If mode is cloud, cloud_endpoint is required
   810	        if llm.mode == LlmMode::Cloud && llm.cloud_endpoint.is_none() {
   811	            return Err(ConfigError::missing_field(
   812	                "llm.cloud_endpoint",
   813	                "a cloud LLM endpoint URL (e.g., https://api.openai.com/v1) when mode is 'cloud'",
   814	            ));
   815	        }
   816	
   817	        // If mode is cloud, cloud_model is required
   818	        if llm.mode == LlmMode::Cloud && llm.cloud_model.is_none() {
   819	            return Err(ConfigError::missing_field(
   820	                "llm.cloud_model",
   821	                "a cloud model name (e.g., 'gpt-4o-mini', 'claude-3-sonnet') when mode is 'cloud'",
   822	            ));
   823	        }
   824	
   825	        // Validate local_endpoint URL format if provided
   826	        if let Some(ref endpoint) = llm.local_endpoint {
   827	            if !is_valid_url(endpoint) {
   828	                return Err(ConfigError::invalid_value(
   829	                    "llm.local_endpoint",
   830	                    "must be a valid URL with host",
   831	                    "a URL like http://localhost:11434",
   832	                ));
   833	            }
   834	        }
   835	
   836	        // Validate cloud_endpoint URL format if provided
   837	        if let Some(ref endpoint) = llm.cloud_endpoint {
   838	            if !is_valid_url(endpoint) {
   839	                return Err(ConfigError::invalid_value(
   840	                    "llm.cloud_endpoint",
   841	                    "must be a valid URL with host",
   842	                    "a URL like https://api.openai.com/v1",
   843	                ));
   844	            }
   845	        }
   846	    }
   847	
   848	    // Validate Jira endpoint URL format if present
   849	    if let Some(ref jira) = config.jira {
   850	        if !is_valid_url(&jira.endpoint) {
   851	            return Err(ConfigError::invalid_value(
   852	                "jira.endpoint",
   853	                "must be a valid URL with host",
   854	                "a URL like https://jira.example.com",
   855	            ));
   856	        }
   857	    }
   858	
   859	    // Validate Squash endpoint URL format if present
   860	    if let Some(ref squash) = config.squash {
   861	        if !is_valid_url(&squash.endpoint) {
   862	            return Err(ConfigError::invalid_value(
   863	                "squash.endpoint",
   864	                "must be a valid URL with host",
   865	                "a URL like https://squash.example.com",
   866	            ));
   867	        }
   868	    }
   869	
   870	    // Validate template paths if provided
   871	    if let Some(ref templates) = config.templates {
   872	        if let Some(ref cr) = templates.cr {
   873	            if !is_valid_path_format(cr) {
   874	                return Err(ConfigError::invalid_value(
   875	                    "templates.cr",
   876	                    "must be a valid file path",
   877	                    "a path like './templates/cr.md'",
   878	                ));
   879	            }
   880	            if !has_valid_extension(cr, &[".md"]) {
   881	                return Err(ConfigError::invalid_value(
   882	                    "templates.cr",
   883	                    "must be a Markdown file",
   884	                    "a .md file path like './templates/cr.md'",
   885	                ));
   886	            }
   887	        }
   888	        if let Some(ref ppt) = templates.ppt {
   889	            if !is_valid_path_format(ppt) {
   890	                return Err(ConfigError::invalid_value(
   891	                    "templates.ppt",
   892	                    "must be a valid file path",
   893	                    "a path like './templates/ppt.pptx'",
   894	                ));
   895	            }
   896	            if !has_valid_extension(ppt, &[".pptx"]) {
   897	                return Err(ConfigError::invalid_value(
   898	                    "templates.ppt",
   899	                    "must be a PowerPoint file",
   900	                    "a .pptx file path like './templates/report.pptx'",
   901	                ));
   902	            }
   903	        }
   904	        if let Some(ref anomaly) = templates.anomaly {
   905	            if !is_valid_path_format(anomaly) {
   906	                return Err(ConfigError::invalid_value(
   907	                    "templates.anomaly",
   908	                    "must be a valid file path",
   909	                    "a path like './templates/anomaly.md'",
   910	                ));
   911	            }
   912	            if !has_valid_extension(anomaly, &[".md"]) {
   913	                return Err(ConfigError::invalid_value(
   914	                    "templates.anomaly",
   915	                    "must be a Markdown file",
   916	                    "a .md file path like './templates/anomaly.md'",
   917	                ));
   918	            }
   919	        }
   920	    }
   921	
   922	    Ok(())
   923	}
