        if let Some(bracket_end) = host_port.find(']') {
            let ipv6_addr = &host_port[1..bracket_end];

            // Basic IPv6 validation: must contain colons
            if ipv6_addr.is_empty() || !ipv6_addr.contains(':') {
                return false;
            }

