   586	/// Detect which configuration section an unknown field belongs to based on serde_yaml's
   587	/// "expected one of" list in the error message.
   588	///
   589	/// This is more reliable than simple string contains checks because it uses the
   590	/// actual valid field names enumerated by serde.
   591	fn detect_section_from_expected_fields(err_msg: &str) -> &'static str {
   592	    // Look for "expected one of" pattern which lists valid fields
   593	    if let Some(expected_pos) = err_msg.find("expected one of") {
   594	        let expected_section = &err_msg[expected_pos..];
   595	
   596	        // Check for jira-specific fields (endpoint, token)
   597	        // Jira has only endpoint and token, so if we see both, it's jira
   598	        if expected_section.contains("`endpoint`") && expected_section.contains("`token`")
   599	           && !expected_section.contains("`username`") && !expected_section.contains("`mode`") {
   600	            return "jira";
   601	        }
   602	
   603	        // Check for squash-specific fields (endpoint, username, password)
   604	        if expected_section.contains("`endpoint`") && expected_section.contains("`username`")
   605	           && expected_section.contains("`password`") {
   606	            return "squash";
   607	        }
   608	
   609	        // Check for llm-specific fields (mode, local_endpoint, api_key, etc.)
   610	        if expected_section.contains("`mode`") && expected_section.contains("`local_endpoint`") {
   611	            return "llm";
   612	        }
   613	
   614	        // Check for templates-specific fields (cr, ppt, anomaly)
   615	        if expected_section.contains("`cr`") && expected_section.contains("`ppt`")
   616	           && expected_section.contains("`anomaly`") {
   617	            return "templates";
   618	        }
   619	
   620	        // Check for root-level fields (project_name, output_folder, jira, squash, etc.)
   621	        if expected_section.contains("`project_name`") && expected_section.contains("`output_folder`") {
   622	            return "root";
   623	        }
   624	    }
   625	
   626	    // Fallback: if no "expected one of", use simple heuristics as backup
   627	    // but be more conservative to avoid mis-attribution
   628	    "root"
