   631	/// Validate that a URL has a valid format (scheme + valid host at minimum)
   632	///
   633	/// A valid host must be either:
   634	/// - "localhost" (with optional port)
   635	/// - A single-label internal hostname (e.g., "jira", "squash", "server1") - RFC 1123 compliant
   636	/// - A multi-label domain name (e.g., "example.com", "api.example.com")
   637	/// - An IPv4 address (contains dots)
   638	/// - An IPv6 address in brackets (e.g., "[::1]", "[2001:db8::1]")
   639	///
   640	/// Single-label hostnames (without dots) are accepted for internal/corporate networks
   641	/// where DNS resolution handles short names (e.g., "http://jira:8080").
   642	///
   643	/// Invalid hosts like "...", "-start", "end-" are rejected.
   644	/// Ports must be valid (1-65535).
   645	fn is_valid_url(url: &str) -> bool {
   646	    // Must start with http:// or https://
   647	    let without_scheme = if let Some(rest) = url.strip_prefix("https://") {
   648	        rest
   649	    } else if let Some(rest) = url.strip_prefix("http://") {
   650	        rest
   651	    } else {
   652	        return false;
   653	    };
   654	
   655	    // Must have at least one character after the scheme (the host)
   656	    // and the host must not be empty or just whitespace
   657	    let trimmed = without_scheme.trim();
   658	    if trimmed.is_empty() {
   659	        return false;
   660	    }
   661	
   662	    // Extract host and port (before path)
   663	    let host_port = trimmed.split('/').next().unwrap_or(trimmed);
   664	
   665	    // Handle IPv6 addresses: [::1] or [::1]:8080
   666	    if host_port.starts_with('[') {
   667	        // Find closing bracket
   668	        if let Some(bracket_end) = host_port.find(']') {
   669	            let ipv6_addr = &host_port[1..bracket_end];
   670	
   671	            // Basic IPv6 validation: must contain colons
   672	            if ipv6_addr.is_empty() || !ipv6_addr.contains(':') {
   673	                return false;
   674	            }
   675	
   676	            // Validate IPv6 address characters and structure
   677	            // IPv6 addresses can contain: hex digits (0-9, a-f, A-F), colons, and optionally
   678	            // a zone ID suffix starting with % (e.g., fe80::1%eth0)
   679	            let (addr_part, _zone_part) = if let Some(zone_pos) = ipv6_addr.find('%') {
   680	                (&ipv6_addr[..zone_pos], Some(&ipv6_addr[zone_pos + 1..]))
   681	            } else {
   682	                (ipv6_addr, None)
   683	            };
   684	
   685	            // The address part must only contain hex digits and colons
   686	            if !addr_part.chars().all(|c| c.is_ascii_hexdigit() || c == ':') {
   687	                return false;
   688	            }
   689	
   690	            // Must have at least 2 colons (minimum valid IPv6 like ::1)
   691	            if addr_part.matches(':').count() < 2 {
   692	                return false;
   693	            }
   694	
   695	            // Check for port after bracket
   696	            let after_bracket = &host_port[bracket_end + 1..];
   697	            if !after_bracket.is_empty() {
   698	                // Must be :port
   699	                if let Some(port_str) = after_bracket.strip_prefix(':') {
   700	                    if port_str.is_empty() {
   701	                        return false; // Empty port is invalid (e.g., "[::1]:")
   702	                    }
   703	                    match port_str.parse::<u32>() {
   704	                        Ok(p) if (1..=65535).contains(&p) => {}
   705	                        _ => return false, // Invalid port number
   706	                    }
   707	                } else {
   708	                    return false; // Invalid format after bracket
   709	                }
   710	            }
   711	            return true;
   712	        } else {
   713	            return false; // Unclosed bracket
   714	        }
   715	    }
   716	
   717	    // Non-IPv6: Check for port and validate it
   718	    let (host, port_str) = if let Some(colon_pos) = host_port.rfind(':') {
   719	        (&host_port[..colon_pos], Some(&host_port[colon_pos + 1..]))
   720	    } else {
   721	        (host_port, None)
   722	    };
   723	
   724	    // Validate port if present (1-65535 range)
   725	    // Reject empty port (e.g., "https://example.com:")
   726	    if let Some(port) = port_str {
   727	        if port.is_empty() {
   728	            return false; // Empty port is invalid (e.g., "https://example.com:")
   729	        }
   730	        match port.parse::<u32>() {
   731	            Ok(p) if (1..=65535).contains(&p) => {}
   732	            _ => return false, // Invalid port number
   733	        }
   734	    }
   735	
   736	    // Host must not be empty after extraction
   737	    if host.is_empty() {
   738	        return false;
   739	    }
   740	
   741	    // Accept "localhost" as valid
   742	    if host.eq_ignore_ascii_case("localhost") {
   743	        return true;
   744	    }
   745	
   746	    // For hosts without dots (internal hostnames like "jira", "squash", "server1"):
   747	    // Accept if it's a valid RFC 1123 label (alphanumeric + hyphens, 1-63 chars,
   748	    // cannot start/end with hyphen)
   749	    if !host.contains('.') {
   750	        // Single-word internal hostname: validate as a single label
   751	        if host.is_empty() || host.len() > 63 {
   752	            return false;
   753	        }
   754	        if host.starts_with('-') || host.ends_with('-') {
   755	            return false;
   756	        }
   757	        // Must be alphanumeric + hyphens only
   758	        return host.chars().all(|c| c.is_ascii_alphanumeric() || c == '-');
   759	    }
