}

impl fmt::Debug for LlmConfig {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        // Redact sensitive query params in cloud_endpoint if present
        let redacted_cloud_endpoint = self
            .cloud_endpoint
            .as_ref()
