        }
        if host.starts_with('-') || host.ends_with('-') {
            return false;
        }
        // Must be alphanumeric + hyphens only
        return host.chars().all(|c| c.is_ascii_alphanumeric() || c == '-');
    }

    // Reject hosts that are just dots (e.g., "...", ".")
    if host.chars().all(|c| c == '.') {
